(764, NULL, 'import', '10251', 'Computer Technology', 'Computer Technology courses introduce students to the features, functions, and design of computer hardware and provide instruction in the maintenance and repair of computer components and peripheral devices.', 'No Change', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 1, 1, '2020-05-28 11:22:16', '2020-05-28 11:22:16'),
(765, NULL, 'import', '10252', 'Computer Maintenance', 'Computer Maintenance courses prepare students to apply basic electronic theory and principles in diagnosing and repairing personal computers and input/output devices. Topics may include operating, installing, maintaining, and repairing computers, network systems, digital control instruments, programmable controllers, and processors.', 'No Change', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 1, 1, '2020-05-28 11:22:16', '2020-05-28 11:22:16'),
(766, NULL, 'import', '10253', 'Information Support and Services', 'Information Support and Services courses prepare students to assist users of personal computers by diagnosing their problems in using application software packages and maintaining security requirements.', 'No Change', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 1, 1, '2020-05-28 11:22:16', '2020-05-28 11:22:16');
