(794, 'import', 'AZ06', 'Foreign Language and Literature', '995', 'Foreign Language and Literature—Aide', 'World Language and Literature—Aide courses offer students the opportunity to assist instructors in preparing, organizing or delivering course curricula. Students may provide tutorial or instructional assistance to other students.', '', NULL, NULL, '', NULL, '', '0', '0', '0', '1', '0', '40664', NULL, '1', '24995', '24', 'Foreign Language and Literature', '995', 'World Language and Literature—Aide \n', 'World Language and Literature—Aide courses offer students the opportunity to assist instructors in preparing, organizing or delivering course curricula. Students may provide tutorial or instructional assistance to other students.\n', '0', '0', '0', '1', NULL, 1, 1, '2020-06-09 09:45:02', '2020-06-09 09:45:02'),
(795, 'import', 'AZ06', 'Foreign Language and Literature', '997', 'Foreign Language and Literature—Independent Study', 'World Language and Literature—Independent Study courses, often conducted with instructors as mentors, enable students to explore (non-English) world language-related topics of interest. Independent Study courses may serve as an opportunity for students to expand their expertise in a particular language, to explore a topic in greater detail, or to develop more advanced skills.', '', NULL, NULL, '', NULL, '', '0', '0', '0', '1', '0', '40664', NULL, '1', '24997', '24', 'Foreign Language and Literature', '997', 'World Language and Literature—Independent Study \n', 'World Language and Literature—Independent Study courses, often conducted with instructors as mentors, enable students to explore (non-English) world language-related topics of interest. Independent Study courses may serve as an opportunity for students to expand their expertise in a particular language, to explore a topic in greater detail, or to develop more advanced skills.\n', '0', '0', '0', '1', NULL, 1, 1, '2020-06-09 09:45:02', '2020-06-09 09:45:02'),
(796, 'import', 'AZ06', 'Foreign Language and Literature', '998', 'Foreign Language and Literature—Workplace Experience', 'World Language and Literature—Workplace Experience courses provide students with work experience in a field related to a (non-English) world language or literature. Goals are typically set cooperatively by the student, teacher, and employer (although students are not necessarily paid). These courses may include classroom activities as well, involving further study of the field or discussion regarding experiences that students encounter in the workplace.', '', NULL, NULL, '', NULL, '', '0', '0', '0', '1', '0', '40664', NULL, '1', '24998', '24', 'Foreign Language and Literature', '998', 'World Language and Literature—Workplace Experience \n', 'World Language and Literature—Workplace Experience courses provide students with work experience in a field related to a (non-English) world language or literature. Goals are typically set cooperatively by the student, teacher, and employer (although students are not necessarily paid). These courses may include classroom activities as well, involving further study of the field or discussion regarding experiences that students encounter in the workplace.\n', '0', '0', '0', '1', NULL, 1, 1, '2020-06-09 09:45:02', '2020-06-09 09:45:02'),
