(2145, 'import', 'AZ61', 'Communications and Audio/Visual Technology (prior-to-secondary)', '151', 'Digital Media Technology', 'These courses are designed to give students the skills necessary to support and enhance their learning about digital medical technology. Topics covered in the course may include internet research, copyright laws, web-publishing, use of digital imagery, electronic forums, newsgroups, mailing lists, presentation tools, and project planning.', '', NULL, NULL, '', '', '', '0', '0', '1', '0', '0', '40664', NULL, '1', '61151', '61', 'Communications and Audio/Visual Technology (prior-to-secondary)', '151', 'Digital Media Technology', 'These courses are designed to give students the skills necessary to support and enhance their learning about digital medical technology. Topics covered in the course may include internet research, copyright laws, web-publishing, use of digital imagery, electronic forums, newsgroups, mailing lists, presentation tools, and project planning.', '0', '0', '1', '0', '0', 1, 1, '2020-06-09 09:45:07', '2020-06-09 09:45:07'),
(2146, 'import', 'AZ61', 'Communications and Audio/Visual Technology (prior-to-secondary)', '152', 'Desktop Publishing', 'Desktop Publishing courses integrate the knowledge and skills learning in word processing with the concepts, procedures and application of desktop publishing. Students learn to format, create and proofread brochures, programs, newsletters, web pages, presentations and manuscripts.', '', NULL, NULL, '', '', '', '0', '0', '1', '0', '0', '40664', NULL, '1', '61152', '61', 'Communications and Audio/Visual Technology (prior-to-secondary)', '152', 'Desktop Publishing', 'Desktop Publishing courses integrate the knowledge and skills learning in word processing with the concepts, procedures and application of desktop publishing. Students learn to format, create and proofread brochures, programs, newsletters, web pages, presentations and manuscripts.', '0', '0', '1', '0', '0', 1, 1, '2020-06-09 09:45:07', '2020-06-09 09:45:07'),
